mapper.select:
select
u.id,username,password,u.phone,u.dept_id deptId,real_name realName,
nick_name nickName,d.name deptName,email,u.status,sex,u.deleted,
u.create_id createId,u.update_id updateId,u.create_time createTime,
create_where createWhere,u.update_time updateTime
from sys_user u inner join sys_dept d on u.dept_id = d.id

    and u.deleted = 1

        and username = #{username}


        and u.id = #{userId}


        and nick_name like concat('%',#{nickName},'%')


        and u.status = #{status}



            and u.create_time between #{startTime} and #{endTime}
